    def load(self, file):
        try:
            f = open(file, 'rb')
            self.net = pickle.load(f)
            f.close()
        except:
            print("No brain file found: %s" % file)
            pass

    def save(self, file):
        f = open(file, 'wb')
        pickle.dump(self.net, f)
        f.close()

    def addToDataSet(self, pixelString, outputs):
        self.inputs.append([float(p) for p in pixelString])
        self.outputs.append(outputs)


    def loadTrainer(self):
        pass

    def train(self):
        self.inputs = np.array(self.inputs)
        self.outputs = np.array(self.outputs)
        
        return self.net.train(self.inputs, self.outputs, epochs=1, show=False)

    def activate(self, pixelString):
        input = np.array([[float(p) for p in pixelString]])
        
        outputs = self.net.sim(input)[0].tolist()
        
        return outputs
