charLines = open('characters.txt','r').readlines()

charset = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ"

inputs  = []
outputs = []

print("Getting samples")

for line in charLines:
    line = line.strip()
    info = line.split("\t")
    
    expectedLetter = info[0]
    
    
    if expectedLetter not in charset:
        continue
    pixelString = info[1]
    
    inputs.append( [float(p) for p in pixelString] )
    
    outputArray = [0.0] * 36
    outputArray[charset.index(expectedLetter)] = 1.0
    
    outputs.append( outputArray )

    
inputs = np.array(inputs)
outputs = np.array(outputs)
